    const MNE_KEY_RANDOM = new MnemonicKey();
    const MNE_KEY_ARR = MNE_KEY_RANDOM.mnemonic.split(' ');
    const [mnemonic, setMnemonic] = UseState({
      1: "one"
    });
    //const [mnemonic_arr, MNE_KEY_ARR2] = UseState();
