    const word = MNE_KEY_RANDOM.mnemonic.split(' ')[0];
    const MNE_KEY_ARR = MNE_KEY_RANDOM.mnemonic.split(' ');
    chageMnemonic(word)
    console.log("handleChangeMnemonic")
  }
  
  console.log(mnemonic)


  function makeMenmonic() {
